import logo from './logo.png';
import './App.css';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import React from 'react';
import Autocomplete from '@material-ui/lab/Autocomplete';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '20ch',
    },
  },
}));

const tripType = [
  {
    value: 'oneWay',
    label: 'One Way'
  },
  {
    value: 'rounded',
    label: 'Round Trip'
  }
];


function App() {
  const classes = useStyles();
  const [currency, setCurrency] = React.useState('oneWay');

  const handleChange = (event) => {
    setCurrency(event.target.value);
  };
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p> Fly At Will </p>
        </header>
        <form className={classes.root} noValidate autoComplete="off">
          <Autocomplete
            options={airports}
            getOptionLabel={(option) => option.title}
            style={{ width: 200 }}
            renderInput={(params) => <TextField {...params} label="Departure" name="depart" variant="outlined" />}/>
          <Autocomplete
            options={airports}
            getOptionLabel={(option) => option.title}
            style={{ width: 200 }}
            renderInput={(params) => <TextField {...params} label="Arrival" name="arrival" className={classes.textField} variant="outlined" />}/>
          <TextField name="out"
                       id="date"
                       label="Date Outward"
                       type="date"
                       className={classes.textField}
                       InputLabelProps={{
                       shrink: true,}}/>
          <TextField name="in"
                       id="date"
                       label="Date Inward"
                       type="date"
                       className={classes.textField}
                       InputLabelProps={{
                       shrink: true,}}/>
          <TextField name="trip"
            id="standard-select-currency"
            select
            label="Trip Type"
            value={currency}
            onChange={handleChange}
          >
            {tripType.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
          <br/>
          <Button type="submit" variant="contained" color="primary">Submit</Button>
        </form>

    </div>
  );
}

const airports = [
  { title: 'PVG', year: 1994 },
  { title: 'PEK', year: 1972 },
  { title: 'SHA', year: 1973 },
];


export default App;
